const fetchData = async () => {
        try {
          // Obtener datos del usuario desde tu API (usando uid)
          const userResponse = await fetch(`http://localhost:5001/api/users/${user.uid}`);
          const userData = await userResponse.json();
          if (userResponse.ok) {
            setProfile(userData);
          }

          // Obtener los productos del usuario desde tu API (usando uid)
          const productsResponse = await fetch(`http://localhost:5001/api/products/by-user/${user.uid}`);
          const productsData = await productsResponse.json();
          if (productsResponse.ok) {
            setProducts(productsData);
          }

        } catch (err) {
          console.error("Error fetching data:", err);
          setError("No pudimos cargar tus datos. Intenta de nuevo.");
        } finally {
          setDataLoading(false);
        }
      };
