import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../AuthContext';
import { useNavigate } from 'react-router-dom';

const AddProductPage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [condition, setCondition] = useState('nuevo');
  const [imageFiles, setImageFiles] = useState([]); // Cambiado a un array
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  if (!user) {
    navigate('/signin');
    return null;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const formData = new FormData();
      formData.append('name', productName);
      formData.append('description', description);
      formData.append('price', parseFloat(price));
      formData.append('seller_id', user.uid);
      formData.append('condition', condition);
      formData.append('currency', 'MXN');

      // Itera sobre los archivos de imagen y los adjunta al FormData
      imageFiles.forEach((file) => {
        formData.append('images', file);
      });

      const response = await fetch('http://localhost:5001/api/products/add', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Error al agregar producto');
      }

      setMessage('¬°Producto agregado con √©xito! üéâ');
      setProductName('');
      setDescription('');
      setPrice('');
      setImageFiles([]); // Limpia el array de archivos
      
      setTimeout(() => navigate('/'), 2000);

    } catch (err) {
      console.error("Error adding product:", err);
      setMessage(`Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <motion.div
      className="container mx-auto px-4 py-8 pt-20 md:pt-8"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-3xl font-bold text-gray-800 mb-8 text-center">
        Sube tu Producto
      </h2>
      <div className="bg-white rounded-3xl shadow-xl p-6 md:p-8 max-w-lg mx-auto">
        <form onSubmit={handleSubmit}>
          {/* ... otros campos del formulario ... */}
          <div className="mb-4">
            <label className="block text-gray-700 font-medium mb-2">Im√°genes</label>
            <input
              type="file"
              onChange={(e) => setImageFiles(Array.from(e.target.files))}
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/30"
              accept="image/*"
              multiple // Permite seleccionar m√∫ltiples archivos
              required
            />
          </div>
          {message && (
            <div className={`mb-4 p-3 rounded-xl text-center font-semibold ${
              message.includes('Error') ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'
            }`}>
              {message}
            </div>
          )}
          <motion.button
            type="submit"
            className="w-full mt-6 px-6 py-4 bg-blue-600 text-white rounded-xl shadow-lg hover:bg-blue-700 transition-colors duration-300 flex items-center justify-center font-semibold text-lg disabled:opacity-50"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            disabled={loading}
          >
            {loading ? 'Subiendo...' : 'Publicar Producto'}
          </motion.button>
        </form>
      </div>
    </motion.div>
  );
};

export default AddProductPage;
